/* eslint-disable import/no-anonymous-default-export */
import React from "react";
import {Sketch,p5} from "react-p5";


export default (p5) => {



    var createMeteor = {
        //Meteor constructor
          meteor: {
              elements: [],
              create: function() {
                // 90% small Meteor(white),10 % huge Meteor
                Math.random() > 0.9
                  ? bigMeteor()
                  : smallMeteor();
              },
              draw: function() {
                  for (var i = 0; i < createMeteor.meteor.elements.length; i++) {
                      createMeteor.meteor.elements[i].move();
                    //after meteor fall outside the windows, remove remove and pop out from the array
                      if (createMeteor.meteor.elements[i].remove) {
                          createMeteor.meteor.elements.splice(i, 1);
                      }
                  }
              }
          },
          colorPreset: {
              colors: {
                  "blue": {
                      fill: [80, 140, 255],
                      stroke: [74, 137, 220]
                  },
                  "firework": {
                      fill: [255, 180, 50],
                      stroke: [246, 187, 66]
                  },
                  "white": {
                      fill: [250, 250, 250],
                      stroke: [230, 233, 237]
                  }
              }
          }
      }
      
      
      let fireworkSound;
      let smallMetetorSound;
      let bigMetetorSound;
      //loading sound for different effect
      function preload() {
        // Load the sound file.
        p5.soundFormats("wav");
        fireworkSound = p5.loadSound("./firework.wav");
        smallMetetorSound = p5.loadSound("./small.wav");
        bigMetetorSound = p5.loadSound("./big.wav");
      
      }
      function MouseFirework(){
        // press mouse to fire a firewok in the sky!
        // eslint-disable-next-line no-undef
        // for(i=-PI; i<=p5.PI;i+=0.2){
        // }
        createMeteor.meteor.elements.push(new Meteor('firework', true, 2))

        // fireworkSound.play();
      }
      
      function smallMeteor(){
        // create small (white) meteor
        createMeteor.meteor.elements.push(new Meteor('white'))
        // smallMetetorSound.play();
      }
      
      function bigMeteor(){
          // create big (blue) meteor
        createMeteor.meteor.elements.push(new Meteor('blue'))
        // bigMetetorSound.play();
      }
      
      
      class Meteor {
        constructor(color, firework, randomAngle) {
            this.size =Math.random(10, 30);
            // is used as the firework, then the position x and y should be the mouse position
            this.x = firework ? p5.mouseX : Math.random(p5.windowHeight, p5.windowWidth);
            this.y = firework ? p5.mouseY : Math.random(-p5.windowHeight / 2, p5.windowWidth / 2);;
            this.px = this.x;
            this.py = this.y;
            // is used as the firework, then Angle should be customizable
            this.randomAngle = firework ? randomAngle : Math.PI / 60;
            // some difference on drop location
            this.dropAngle = Math.PI / -6;
            //random speed
            this.speed = Math.random(15, 25);
            // add the random difference into the dropAngle
            this.r = this.dropAngle + Math.random(-30 * this.randomAngle, 10 * this.randomAngle) / 10;
            this.d = 2;
            this.remove = false;
            this.move = function () {
                this.px = this.x + Math.sin(this.r) * this.d;
                this.py = this.y + Math.cos(this.r) * this.d;
                //Trail
                fill(createMeteor.colorPreset.colors[color].fill[0], createMeteor.colorPreset.colors[color].fill[1], createMeteor.colorPreset.colors[color].fill[2], 120);
                //use beginShape() and endShape() to create a shape of the trail for meteor
                p5.beginShape();
                p5.vertex(this.x + Math.sin(this.r) * (this.d - this.size * 6), this.y + Math.cos(this.r) * (this.d - this.size * 6));
                var i_r = 90 + this.r;
                p5.vertex(this.px + Math.sin(i_r) * this.size / 2, this.py + Math.cos(i_r) * this.size / 2);
                p5.vertex(this.px - Math.sin(i_r) * this.size / 2, this.py - Math.cos(i_r) * this.size / 2);
                p5.endShape();
                //Meteor head
                p5.fill(createMeteor.colorPreset.colors[color].fill[0], createMeteor.colorPreset.colors[color].fill[1], createMeteor.colorPreset.colors[color].fill[2]);
                p5.ellipse(this.px, this.py, this.size, this.size);
                // with this.d 's '
                this.d += this.speed;
                // when the meteor fly outside of the windows, remove the element
                this.remove = (this.py > 1.5 * p5.windowHeight);
            };
        }
    }
      
    
    

	const setup = (p5, canvasParentRef) => {
        p5.createCanvas(p5.windowWidth, p5.windowHeight);
        p5.noSmooth();
        setInterval(function() {
            createMeteor.meteor.create()
        }, 500);
    }
    
	const draw = (p5) => {
        p5.clear(); 
        createMeteor.meteor.draw();
    }

    const mousePressed = (p5) => {
      MouseFirework()
    }

    return <Sketch setup={setup} draw={draw} />;

}
